import React, { useState } from "react";
import "./AuthForm.css";
import { useDispatch, useSelector } from "react-redux";
import { useGlobalContext } from "../../Context/AuthContext";
import { useHistory } from "react-router-dom";

const SignIn = () => {
  const [error, setError] = useState("");
  const history = useHistory();
  const { signUp } = useGlobalContext();

  const showModal = useSelector((state) => state);
  const dispatch = useDispatch();

  const toggleClose = () => {
    dispatch({
      type: "CLOSEMODAL",
    });
  };

  return (
    <div className={showModal.showSignIn ? "global-modal" : "hide-modal"}>
      <div onClick={toggleClose} className="overlay">
        <div className="container-modal">
          <form className="form-auth">
            <h2>Connexion</h2>
            <label htmlFor="mail">Votre email</label>
            <input type="email" id="mail" required />

            <label htmlFor="password">Votre mot de passe</label>
            <input type="password" id="password" required />

            <button className="btn-sign">Se connecter</button>
          </form>
          <button onClick={toggleClose} className="btn-close">
            X
          </button>
          <p className="bottom-help-txt">Besoin de cr√©er un compte</p>
        </div>
      </div>
    </div>
  );
};

export default SignIn;
