import React from "react";
import "./AuthForm.css";
import { useDispatch, useSelector } from "react-redux";

const SignUp = () => {
  const showModal = useSelector((state) => state);
  const dispatch = useDispatch();

  const toggleClose = () => {
    dispatch({
      type: "CLOSEMODAL",
    });
  };

  return (
    <div className={showModal.showSignUp ? "global-modal" : "hide-modal"}>
      <div onClick={toggleClose} className="overlay">
        <div className="container-modal">
          <form className="form-auth">
            <h2>Inscription</h2>
            <label htmlFor="mail">Votre email</label>
            <input type="email" id="mail" required />

            <label htmlFor="password">Votre mot de passe</label>
            <input type="password" id="password" required />

            <label htmlFor="confirmpassword">Confirmer le mot de passe</label>
            <input type="password" id="confirmpassword" required />

            <button className="btn-sign">S'inscrire</button>
          </form>
          <button onClick={toggleClose} className="btn-close">
            X
          </button>
          <p className="bottom-help-txt">Vous avez déjà un compte</p>
        </div>
      </div>
    </div>
  );
};

export default SignUp;
